
public class ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N=10;
		int resultado=0;
		
		resultado=N+77;
		System.out.println("Al incrementar en 77 el resultado es "+resultado);
		resultado=N-3;
		System.out.println("Al decrementar en -3 el resultado es "+resultado);
		resultado=N*2;
		System.out.println("Al duplicar su valor es "+resultado);
	}

}
